import React, {useState} from "react";

function CreateArea(props) {

    const[newNote, setNewNote] = useState({title: "", content: ""});

    function handleNewNote(event){

const {name, value} = event.target;
setNewNote(prevValue =>{
  return{
    ...prevValue, 
    [name] : value
  }
})
    
}
function submitNote(event){
event.preventDefault();
setNewNote()
props.onAdd(newNote);
}

  return (
    <div>
      <form>
        <input onChange={handleNewNote} name="title" placeholder="Title" value={newNote.title} />
        <textarea onChange={handleNewNote} name="content" placeholder="Take a note..." rows="3" value={newNote.content} />
        <button onClick={submitNote}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
